--
-- Cấu trúc bảng cho bảng `categories`
--

CREATE TABLE `categories` (
  `id` int(11) NOT NULL,
  `name` varchar(255) NOT NULL,
  `description` text DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--
-- Đang đổ dữ liệu cho bảng `categories`
--

INSERT INTO `categories` (`id`, `name`, `description`) VALUES
(1, 'Rượu vang', 'Rượu vang là đồ uống có cồn được lên men trực tiếp từ trái nho chín trong tự nhiên. Về mặt bản chất, chỉ có rượu được lên men từ trái nho mới được gọi là rượu vang nhưng ngày nay, người ta sáng tạo ra rất nhiều công thức đồ uống khác nhau nên một số thức uống được lên men từ các loại quả như táo, mận… cũng được gọi là vang.'),
(2, 'Whisky', 'Whisky là một loại đồ uống có chứa cồn được sản xuất từ ngũ cốc bằng cách lên men và chưng cất. Từ Whisky được nhắc đến lần đầu tiên vào năm 1736 xuất phát từ usisge beatha trong tiếng Gaelic tại Scotland hay từ uisce beatha trong tiếng Gaelic tại Ireland và có nghĩa là \"nước của cuộc sống\". Whisky, \"Water of Life\" từ người mới tiếp cận đến người sành rượu và ngay cả các chuyên gia thẩm rượu, đều cho Whisky là thứ đồ uống tinh túy và lan tỏa thăng hoa.'),
(3, 'Cognac', 'Rượu Cognac (hay Cô-nhắc) là một loại rượu mạnh sản xuất tại vùng Cognac của Pháp, được chưng cất từ loại rượu nhẹ sản sinh trong tiến trình lên men nho quả, sau một thời gian ủ trong thùng gỗ sồi được đem ra đóng chai và bán khắp thế giới. Cognac có thể xem như là loại thức uống tinh túy của thời gian. Hơn 3 thế kỷ qua, Cognac được biết rộng rãi trên toàn cầu và cũng là rượu ngon nhất chưng cất từ nho. Hàng năm có khoảng hơn 20 triệu chai Cognac được xuất xưởng, nhưng chỉ có 3% trong số đó được uống ở Pháp.'),
(4, 'Vodka', 'Rượu Vodka vẫn là dòng rượu mạnh thống trị hàng đầu trên toàn cầu, và sự thống trị này chưa có dấu hiệu giảm bớt. Bởi vì Vodka ngoài uống neat còn truyền cảm hứng cho nhiều loại cocktail hơn bất cứ dòng rượu nào.Theo truyền thống, nó được tạo ra bằng cách chưng cất chất lỏng từ các loại ngũ cốc hoặc khoai tây đã được lên men, mặc dù một số thương hiệu hiện đại sử dụng trái cây hoặc đường để lên men.'),
(5, 'Rượu mùi', 'Rượu mùi hay rượu hương là các loại rượu được pha chế từ một thức uống có cồn mà được chưng cất, được bỏ thêm với những vị từ trái cây, kem, dược thảo, gia vị, bông hoa hay các loại hạch, có thêm đường hoặc các chất tạo ra vị ngọt.\r\nCác chủng loại rượu trên thế giới thì nhiều vô số kể. Trong số này không thể không kể đến dòng rượu Liqueur/Tequila/Gin/Rum tự hào danh trấn thiên hạ, đóng đô ở bất cứ kệ rượu nào trong quán bar'),
(6, 'Hộp quà tết', 'Tết là văn hoá truyền thống của người Việt, là dịp để gắn kết tình thân gia đình, tết còn là cơ hội để các doanh nghiệp quan tâm, thể hiện tình cảm của mình với đối tác, với nhân viên của mình bằng những món quà tết, hộp quà tết ý nghĩa. Vì đối tác, nhân viên là tài sản vô giá của doanh nghiệp cần gắn kết xây dựng.');

